(171, 'wpcode_snippets', 'a:1:{s:10:\"everywhere\";a:1:{i:0;a:14:{s:2:\"id\";i:7;s:5:\"title\";s:47:\"Enable upload SVG & Completely Disable Comments\";s:4:\"code\";s:3010:\"/**\r\n * Allow SVG uploads for administrator users.\r\n *\r\n * @param array $upload_mimes Allowed mime types.\r\n *\r\n * @return mixed\r\n */\r\nadd_filter(\r\n	\'upload_mimes\',\r\n	function ( $upload_mimes ) {\r\n		// By default, only administrator users are allowed to add SVGs.\r\n		// To enable more user types edit or comment the lines below but beware of\r\n		// the security risks if you allow any user to upload SVG files.\r\n		if ( ! current_user_can( \'administrator\' ) ) {\r\n			return $upload_mimes;\r\n		}\r\n\r\n		$upload_mimes[\'svg\']  = \'image/svg+xml\';\r\n		$upload_mimes[\'svgz\'] = \'image/svg+xml\';\r\n\r\n		return $upload_mimes;\r\n	}\r\n);\r\n\r\n/**\r\n * Add SVG files mime check.\r\n *\r\n * @param array        $wp_check_filetype_and_ext Values for the extension, mime type, and corrected filename.\r\n * @param string       $file Full path to the file.\r\n * @param string       $filename The name of the file (may differ from $file due to $file being in a tmp directory).\r\n * @param string[]     $mimes Array of mime types keyed by their file extension regex.\r\n * @param string|false $real_mime The actual mime type or false if the type cannot be determined.\r\n */\r\nadd_filter(\r\n	\'wp_check_filetype_and_ext\',\r\n	function ( $wp_check_filetype_and_ext, $file, $filename, $mimes, $real_mime ) {\r\n\r\n		if ( ! $wp_check_filetype_and_ext[\'type\'] ) {\r\n\r\n			$check_filetype  = wp_check_filetype( $filename, $mimes );\r\n			$ext             = $check_filetype[\'ext\'];\r\n			$type            = $check_filetype[\'type\'];\r\n			$proper_filename = $filename;\r\n\r\n			if ( $type && 0 === strpos( $type, \'image/\' ) && \'svg\' !== $ext ) {\r\n				$ext  = false;\r\n				$type = false;\r\n			}\r\n\r\n			$wp_check_filetype_and_ext = compact( \'ext\', \'type\', \'proper_filename\' );\r\n		}\r\n\r\n		return $wp_check_filetype_and_ext;\r\n\r\n	},\r\n	10,\r\n	5\r\n);\r\n\r\nadd_action(\'admin_init\', function () {\r\n    // Redirect any user trying to access comments page\r\n    global $pagenow;\r\n    \r\n    if ($pagenow === \'edit-comments.php\') {\r\n        wp_safe_redirect(admin_url());\r\n        exit;\r\n    }\r\n\r\n    // Remove comments metabox from dashboard\r\n    remove_meta_box(\'dashboard_recent_comments\', \'dashboard\', \'normal\');\r\n\r\n    // Disable support for comments and trackbacks in post types\r\n    foreach (get_post_types() as $post_type) {\r\n        if (post_type_supports($post_type, \'comments\')) {\r\n            remove_post_type_support($post_type, \'comments\');\r\n            remove_post_type_support($post_type, \'trackbacks\');\r\n        }\r\n    }\r\n});\r\n\r\n// Close comments on the front-end\r\nadd_filter(\'comments_open\', \'__return_false\', 20, 2);\r\nadd_filter(\'pings_open\', \'__return_false\', 20, 2);\r\n\r\n// Hide existing comments\r\nadd_filter(\'comments_array\', \'__return_empty_array\', 10, 2);\r\n\r\n// Remove comments page in menu\r\nadd_action(\'admin_menu\', function () {\r\n    remove_menu_page(\'edit-comments.php\');\r\n});\r\n\r\n// Remove comments links from admin bar\r\nadd_action(\'init\', function () {\r\n    if (is_admin_bar_showing()) {\r\n        remove_action(\'admin_bar_menu\', \'wp_admin_bar_comments_menu\', 60);\r\n    }\r\n});\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"everywhere\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:13:\"compiled_code\";s:0:\"\";s:8:\"modified\";s:19:\"2025-04-26 06:23:39\";}}}', 'auto'),
